// activityIndicator.test.tsx
import * as React from 'react';
import { createVariantMap, toJson, shallow } from '../../../tests/globalImports';
import { ActivityIndicator, ActivityIndicatorProps } from './activityIndicator';
import activityIndicatorVariants from './activityIndicator.json';

describe('ActivityIndicator component should be rendered correctly', () => {
  it('All variants', () => {

    const variantMap: { [key: string]: ActivityIndicatorProps } = createVariantMap<ActivityIndicatorProps>(activityIndicatorVariants, ActivityIndicatorProps);
    Object.entries(variantMap).forEach(([key, value]) => {
        const icon = shallow(<ActivityIndicator {...value} />);
        expect(toJson(icon)).toMatchSnapshot();
    });
  });
});