// background.test.tsx
import * as React from 'react';
import { createVariantMap, toJson, shallow } from '../../../tests/globalImports';
import { Background, BackgroundProps } from './background';
import backgroundVariants from './background.json';

describe('Background component should be rendered correctly', () => {
  it('All variants', () => {

    const variantMap: { [key: string]: BackgroundProps } = createVariantMap<BackgroundProps>(backgroundVariants, BackgroundProps);
    Object.entries(variantMap).forEach(([key, value]) => {
        const icon = shallow(<Background {...value} />);
        expect(toJson(icon)).toMatchSnapshot();
    });
  });
});