// button.test.tsx
import * as React from 'react';
import { createVariantMap, toJson, shallow } from '../../../tests/globalImports';
import { Button, ButtonProps } from './button';
import buttonVariants from './button.json';

describe('Button component should be rendered correctly', () => {
  it('All variants', () => {

    const variantMap: { [key: string]: ButtonProps } = createVariantMap<ButtonProps>(buttonVariants, ButtonProps);
    Object.entries(variantMap).forEach(([key, value]) => {
        const button = shallow(<Button {...value} />);
        expect(toJson(button)).toMatchSnapshot();
    });
  });
});