// checkbox.test.tsx
import * as React from 'react';
import { createVariantMap, toJson, shallow } from '../../../tests/globalImports';
import { Checkbox, CheckboxProps } from './checkbox';
import checkboxVariants from './checkbox.json';

describe('Checkbox component should be rendered correctly', () => {
  it('All variants', () => {

    const variantMap: { [key: string]: CheckboxProps } = createVariantMap<CheckboxProps>(checkboxVariants, CheckboxProps);
    Object.entries(variantMap).forEach(([key, value]) => {
        const icon = shallow(<Checkbox {...value} />);
        expect(toJson(icon)).toMatchSnapshot();
    });
  });
});