// chip.test.tsx
import * as React from 'react';
import { createVariantMap, toJson, shallow } from '../../../tests/globalImports';
import { Chip, ChipProps } from './chip';
import buttonVariants from './chip.json';

describe('Chip component should be rendered correctly', () => {
  it('All variants', () => {

    const variantMap: { [key: string]: ChipProps } = createVariantMap<ChipProps>(buttonVariants, ChipProps);
    Object.entries(variantMap).forEach(([key, value]) => {
        const chip = shallow(<Chip {...value} />);
        expect(toJson(chip)).toMatchSnapshot();
    });
  });
});