// divider.test.tsx
import * as React from 'react';
import { createVariantMap, toJson, shallow } from '../../../tests/globalImports';
import { Divider, DividerProps } from './divider';
import dividerVariants from './divider.json';

describe('Divider component should be rendered correctly', () => {
  it('All variants', () => {

    const variantMap: { [key: string]: DividerProps } = createVariantMap<DividerProps>(dividerVariants, DividerProps);
    Object.entries(variantMap).forEach(([key, value]) => {
        const icon = shallow(<Divider {...value} />);
        expect(toJson(icon)).toMatchSnapshot();
    });
  });
});