// icon.test.tsx
import * as React from 'react';
import { createVariantMap, toJson, shallow } from '../../../tests/globalImports';
import { Icon, IconProps } from './icon';
import iconVariants from './icon.json';

describe('Icon component should be rendered correctly', () => {
  it('All variants', () => {

    const variantMap: { [key: string]: IconProps } = createVariantMap<IconProps>(iconVariants, IconProps);
    Object.entries(variantMap).forEach(([key, value]) => {
        const icon = shallow(<Icon {...value} />);
        expect(toJson(icon)).toMatchSnapshot();
    });
  });
});