import * as React from 'react';
import classNames from 'classnames';

class IconProps {
  iconName: string;
  titleText: string;
  className?: string;
}

/**
 * @name    a-icon
 * @type    atom
 * @author Experience One AG
 * @copyright Robert Bosch GmbH
 *
 * @param   {iconName}  string      name of the font icon
 * @param   {string}  titleText     tooltip text of the icon
 * @param   {className} string      additional class which can be passed in
 * component, which uses the bosch icon font
 */
const Icon: React.FunctionComponent<IconProps> = ({
  iconName,
  titleText = '',
  className = '',
}: IconProps) => {
  const iconClass: string = classNames('a-icon', {
    [className]: className,
    [`boschicon-bosch-ic-${iconName}`]: iconName,
  });

  return <i className={`${iconClass}`} title={titleText} />;
};

export { Icon, IconProps };
