// image.test.tsx
import * as React from 'react';
import { createVariantMap, toJson, shallow } from '../../../tests/globalImports';
import { Image, ImageProps } from './image';
import imageVariants from './image.json';

describe('Image component should be rendered correctly', () => {
  it('All variants', () => {

    const variantMap: { [key: string]: ImageProps } = createVariantMap<ImageProps>(imageVariants, ImageProps);
    Object.entries(variantMap).forEach(([key, value]) => {
        const icon = shallow(<Image {...value} />);
        expect(toJson(icon)).toMatchSnapshot();
    });
  });
});