// link.test.tsx
import * as React from 'react';
import { createVariantMap, toJson, shallow } from '../../../tests/globalImports';
import { Link, LinkProps } from './link';
import buttonVariants from './link.json';

describe('Link component should be rendered correctly', () => {
  it('All variants', () => {

    const variantMap: { [key: string]: LinkProps } = createVariantMap<LinkProps>(buttonVariants, LinkProps);
    Object.entries(variantMap).forEach(([key, value]) => {
        const link = shallow(<Link {...value} />);
        expect(toJson(link)).toMatchSnapshot();
    });
  });
});