// list.test.tsx
import * as React from 'react';
import { createVariantMap, toJson, shallow } from '../../../tests/globalImports';
import { List, ListProps } from './list';
import listVariants from './list.json';

describe('List component should be rendered correctly', () => {
  it('All variants', () => {

    const variantMap: { [key: string]: ListProps } = createVariantMap<ListProps>(listVariants, ListProps);
    Object.entries(variantMap).forEach(([key, value]) => {
        const icon = shallow(<List {...value} />);
        expect(toJson(icon)).toMatchSnapshot();
    });
  });
});