// pageIndicator.test.tsx
import * as React from 'react';
import { createVariantMap, toJson, shallow } from '../../../tests/globalImports';
import { PageIndicator, PageIndicatorProps } from './pageIndicator';
import pageIndicatorVariants from './pageIndicator.json';

describe('PageIndicator component should be rendered correctly', () => {
  it('All variants', () => {

    const variantMap: { [key: string]: PageIndicatorProps } = createVariantMap<PageIndicatorProps>(pageIndicatorVariants, PageIndicatorProps);
    Object.entries(variantMap).forEach(([key, value]) => {
        const icon = shallow(<PageIndicator {...value} />);
        expect(toJson(icon)).toMatchSnapshot();
    });
  });
});