// pin.test.tsx
import * as React from 'react';
import { createVariantMap, toJson, shallow } from '../../../tests/globalImports';
import { Pin, PinProps } from './pin';
import pinVariants from './pin.json';

describe('Pin component should be rendered correctly', () => {
  it('All variants', () => {

    const variantMap: { [key: string]: PinProps } = createVariantMap<PinProps>(pinVariants, PinProps);
    Object.entries(variantMap).forEach(([key, value]) => {
        const icon = shallow(<Pin {...value} />);
        expect(toJson(icon)).toMatchSnapshot();
    });
  });
});