// progressIndicator.test.tsx
import * as React from 'react';
import { createVariantMap, toJson, shallow } from '../../../tests/globalImports';
import { ProgressIndicator, ProgressIndicatorProps } from './progressIndicator';
import pinVariants from './progressIndicator.json';

describe('ProgressIndicator component should be rendered correctly', () => {
  it('All variants', () => {

    const variantMap: { [key: string]: ProgressIndicatorProps } = createVariantMap<ProgressIndicatorProps>(pinVariants, ProgressIndicatorProps);
    Object.entries(variantMap).forEach(([key, value]) => {
        const icon = shallow(<ProgressIndicator {...value} />);
        expect(toJson(icon)).toMatchSnapshot();
    });
  });
});