// radioButton.test.tsx
import * as React from 'react';
import { createVariantMap, toJson, shallow } from '../../../tests/globalImports';
import { RadioButton, RadioButtonProps } from './radioButton';
import radioButtonVariants from './radioButton.json';

describe('RadioButton component should be rendered correctly', () => {
  it('All variants', () => {

    const variantMap: { [key: string]: RadioButtonProps } = createVariantMap<RadioButtonProps>(radioButtonVariants, RadioButtonProps);
    Object.entries(variantMap).forEach(([key, value]) => {
        const icon = shallow(<RadioButton {...value} />);
        expect(toJson(icon)).toMatchSnapshot();
    });
  });
});