// searchfield.test.tsx
import * as React from 'react';
import { createVariantMap, toJson, shallow } from '../../../tests/globalImports';
import { Searchfield, SearchfieldProps } from './searchfield';
import searchfieldVariants from './searchfield.json';

describe('Searchfield component should be rendered correctly', () => {
  it('All variants', () => {

    const variantMap: { [key: string]: SearchfieldProps } = createVariantMap<SearchfieldProps>(searchfieldVariants, SearchfieldProps);
    Object.entries(variantMap).forEach(([key, value]) => {
        const icon = shallow(<Searchfield {...value} />);
        expect(toJson(icon)).toMatchSnapshot();
    });
  });
});