// slider.test.tsx
import * as React from 'react';
import { createVariantMap, toJson, shallow } from '../../../tests/globalImports';
import { Slider, SliderProps } from './slider';
import sliderVariants from './slider.json';

describe('Slider component should be rendered correctly', () => {
  it('All variants', () => {

    const variantMap: { [key: string]: SliderProps } = createVariantMap<SliderProps>(sliderVariants, SliderProps);
    Object.entries(variantMap).forEach(([key, value]) => {
        const icon = shallow(<Slider {...value} />);
        expect(toJson(icon)).toMatchSnapshot();
    });
  });
});