// tableCell.test.tsx
import * as React from 'react';
import { createVariantMap, toJson, shallow } from '../../../tests/globalImports';
import { TableCell, TableCellProps } from './tableCell';
import tableCellVariants from './tableCell.json';

describe('TableCell component should be rendered correctly', () => {
  it('All variants', () => {

    const variantMap: { [key: string]: TableCellProps } = createVariantMap<TableCellProps>(tableCellVariants, TableCellProps);
    Object.entries(variantMap).forEach(([key, value]) => {
        const icon = shallow(<TableCell {...value} />);
        expect(toJson(icon)).toMatchSnapshot();
    });
  });
});