// text.test.tsx
import * as React from 'react';
import { createVariantMap, toJson, shallow } from '../../../tests/globalImports';
import { Text, TextProps } from './text';
import textVariants from './text.json';

describe('Text component should be rendered correctly', () => {
  it('All variants', () => {

    const variantMap: { [key: string]: TextProps } = createVariantMap<TextProps>(textVariants, TextProps);
    Object.entries(variantMap).forEach(([key, value]) => {
        const icon = shallow(<Text {...value} />);
        expect(toJson(icon)).toMatchSnapshot();
    });
  });
});