// textArea.test.tsx
import * as React from 'react';
import { createVariantMap, toJson, shallow } from '../../../tests/globalImports';
import { TextArea, TextAreaProps } from './textArea';
import textAreaVariants from './textArea.json';

describe('TextArea component should be rendered correctly', () => {
  it('All variants', () => {

    const variantMap: { [key: string]: TextAreaProps } = createVariantMap<TextAreaProps>(textAreaVariants, TextAreaProps);
    Object.entries(variantMap).forEach(([key, value]) => {
        const icon = shallow(<TextArea {...value} />);
        expect(toJson(icon)).toMatchSnapshot();
    });
  });
});