// textField.test.tsx
import * as React from 'react';
import { createVariantMap, toJson, shallow } from '../../../tests/globalImports';
import { TextField, TextfieldProps } from './textField';
import textAreaVariants from './textField.json';

describe('TextField component should be rendered correctly', () => {
  it('All variants', () => {

    const variantMap: { [key: string]: TextfieldProps } = createVariantMap<TextfieldProps>(textAreaVariants, TextfieldProps);
    Object.entries(variantMap).forEach(([key, value]) => {
        const icon = shallow(<TextField {...value} />);
        expect(toJson(icon)).toMatchSnapshot();
    });
  });
});