// toggle.test.tsx
import * as React from 'react';
import { createVariantMap, toJson, shallow } from '../../../tests/globalImports';
import { Toggle, ToggleProps } from './toggle';
import toggleVariants from './toggle.json';

describe('Toggle component should be rendered correctly', () => {
  it('All variants', () => {

    const variantMap: { [key: string]: ToggleProps } = createVariantMap<ToggleProps>(toggleVariants, ToggleProps);
    Object.entries(variantMap).forEach(([key, value]) => {
        const icon = shallow(<Toggle {...value} />);
        expect(toJson(icon)).toMatchSnapshot();
    });
  });
});