// tooltip.test.tsx
import * as React from 'react';
import { createVariantMap, toJson, shallow } from '../../../tests/globalImports';
import { Tooltip, TooltipProps } from './tooltip';
import toggleVariants from './tooltip.json';

describe('Tooltip component should be rendered correctly', () => {
  it('All variants', () => {

    const variantMap: { [key: string]: TooltipProps } = createVariantMap<TooltipProps>(toggleVariants, TooltipProps);
    Object.entries(variantMap).forEach(([key, value]) => {
        const icon = shallow(<Tooltip {...value} />);
        expect(toJson(icon)).toMatchSnapshot();
    });
  });
});