import * as React from 'react';
import classNames from 'classnames';

class TooltipProps {
  content: string;
  width?: string;
  type?: string;
}

/**
 * @name      a-tooltip
 * @type      atom
 * @author    Experience One AG
 * @copyright Robert Bosch GmbH
 *
 * @param   {content} string   Array of content which should be rendered
 * @param   {width} string            Type of Width definition (Default, Dynamic or Fixed)
 * @param   {type} string      Type of Tooltip definition (Neutral, Success, Error or Warning)
 * @description
 * representation of tooltips
 */

const Tooltip: React.FunctionComponent<TooltipProps> = ({
  content,
  width,
  type,
}: TooltipProps) => {
  const tooltipClass = classNames('a-tooltip', {
    [`a-tooltip--${type}`]: type,
    [`-${width}-width`]: width,
  });

  return (
    <span className={tooltipClass} data-tooltip={content} role="tooltip">
      Example Item to hover
    </span>
  );
};

export { Tooltip, TooltipProps };
