// uiIcon.test.tsx
import * as React from 'react';
import { createVariantMap, toJson, shallow } from '../../../tests/globalImports';
import { UiIcon, UiIconProps } from './uiIcon';
import uiIconVariants from './uiIcon.json';

describe('UiIcon component should be rendered correctly', () => {
  it('All variants', () => {

    const variantMap: { [key: string]: UiIconProps } = createVariantMap<UiIconProps>(uiIconVariants, UiIconProps);
    Object.entries(variantMap).forEach(([key, value]) => {
        const icon = shallow(<UiIcon {...value} />);
        expect(toJson(icon)).toMatchSnapshot();
    });
  });
});