import * as React from 'react';
import classNames from 'classnames';

class UiIconProps {
  name?: string;
  titleText?: string;
  className?: string;
}

/**
 * @name      a-ui-icon
 * @type      atom
 * @author    Experience One AG
 * @copyright Robert Bosch GmbH
 *
 * @param   {name} string      name of the ui icon
 * @param   {titleText} string title text of the ui icon
 * @param   {className} string additional class names
 * @description
 * component, which uses the svg sprite of Bosch ui-icons
 */

const UiIcon: React.FunctionComponent<UiIconProps> = ({
  name,
  titleText = '',
  className = '',
}) => {
  const iconClass = classNames('a-ui-icon', {
    [className]: className,
    [`a-ui-icon--ui-ic-${name}`]: name,
  });
  return <i className={iconClass} title={titleText} />;
};

export { UiIcon, UiIconProps };
