// valueModificator.test.tsx
import * as React from 'react';
import { createVariantMap, toJson, shallow } from '../../../tests/globalImports';
import { ValueModificator, ValueModificatorProps } from './valueModificator';
import valueModificatorVariants from './valueModificator.json';

describe('ValueModificator component should be rendered correctly', () => {
  it('All variants', () => {

    const variantMap: { [key: string]: ValueModificatorProps } = createVariantMap<ValueModificatorProps>(valueModificatorVariants, ValueModificatorProps);
    Object.entries(variantMap).forEach(([key, value]) => {
        const link = shallow(<ValueModificator {...value} />);
        expect(toJson(link)).toMatchSnapshot();
    });
  });
});