// video.test.tsx
import * as React from 'react';
import { createVariantMap, toJson, shallow } from '../../../tests/globalImports';
import { Video, VideoProps } from './video';
import videoVariants from './video.json';

describe('Video component should be rendered correctly', () => {
  it('All variants', () => {

    const variantMap: { [key: string]: VideoProps } = createVariantMap<VideoProps>(videoVariants, VideoProps);
    Object.entries(variantMap).forEach(([key, value]) => {
        const icon = shallow(<Video {...value} />);
        expect(toJson(icon)).toMatchSnapshot();
    });
  });
});