// box.test.tsx
import * as React from 'react';
import { createVariantMap, toJson, shallow } from '../../../../tests/globalImports';
import { Typography, TypographyProps } from './typography';
import typographyVariants from './typography.json';

describe('Typography component should be rendered correctly', () => {
  it('All variants', () => {

    const variantMap: { [key: string]: TypographyProps } = createVariantMap<TypographyProps>(typographyVariants, TypographyProps);
    Object.entries(variantMap).forEach(([key, value]) => {
        const icon = shallow(<Typography {...value} />);
        expect(toJson(icon)).toMatchSnapshot();
    });
  });
});