import * as React from 'react';
import { Button } from '../../../atoms/button/button';
import { Divider } from '../../../atoms/divider/divider';
import { FormField } from '../formField';

interface TextFieldNotificationProps {
  /**
   * As checkbox doesnt have a notification bar and therefore no hint, its
   * neutral state will be the same as an unset state, therefore the checkbox
   * value prevents the neutral button from showing.
   */
  fieldType: 'text' | 'password' | 'textarea' | 'radio' | 'checkbox';
}

const NotificationFormFieldDemonstrator: React.FunctionComponent<TextFieldNotificationProps> = ({
  fieldType,
}) => (
  <div className="frontend-kit-example_form-field-notification">
    <Button label="Error" action="error" />
    <Button label="Warning" action="warning" />
    <Button label="Success" action="success" />

    {fieldType !== 'checkbox' && <Button label="Neutral" action="neutral" />}
    <Button label="Reset" action="reset" type="secondary" />
    <Divider />
    <FormField
      fieldType={fieldType}
      id="frontend-kit-example_form-field-notification"
    />
    <style
      dangerouslySetInnerHTML={{
        __html:
          '.frontend-kit-example_form-field-notification .a-button { margin-right: 12px; display: inline-block; }',
      }}
    />
  </div>
);

export default NotificationFormFieldDemonstrator;
