// formField.test.tsx
import * as React from 'react';
import { createVariantMap, toJson, shallow } from '../../../tests/globalImports';
import { FormField, FormFieldProps } from './formField';
import formFieldVariants from './formField.json';

describe('FormField component should be rendered correctly', () => {
  it('All variants', () => {

    const variantMap: { [key: string]: FormFieldProps } = createVariantMap<FormFieldProps>(formFieldVariants, FormFieldProps);
    Object.entries(variantMap).forEach(([key, value]) => {
        const icon = shallow(<FormField {...value} />);
        expect(toJson(icon)).toMatchSnapshot();
    });
  });
});