import * as React from 'react';
import { Button } from '../../../atoms/button/button';
import { Lightbox } from '../lightbox';

interface ModalLightboxProps {
  lightboxId: string;
  images: [
    {
      srcSet: {
        source: string;
        width: number;
      }[];
      defaultSrc: string;
      altText: string;
      caption: string;
    },
  ];
}

const ModalLightbox: React.FunctionComponent<ModalLightboxProps> = ({
  lightboxId,
  images,
}) => (
  <div
    className="frontend-kit-example_modal-lightbox"
    data-frok-show-lightbox={lightboxId}
  >
    <Button label="click here" action="show" />
    <Lightbox modal lightboxId={lightboxId} images={images} />
  </div>
);

export default ModalLightbox;
