// popover.test.tsx
import * as React from 'react';
import { createVariantMap, toJson, shallow } from '../../../tests/globalImports';
import { Popover, PopoverProps } from './popover';
import popoverVariants from './popover.json';

describe('Popover component should be rendered correctly', () => {
  it('All variants', () => {

    const variantMap: { [key: string]: PopoverProps } = createVariantMap<PopoverProps>(popoverVariants, PopoverProps);
    Object.entries(variantMap).forEach(([key, value]) => {
        const icon = shallow(<Popover {...value} />);
        expect(toJson(icon)).toMatchSnapshot();
    });
  });
});