// table.test.tsx
import * as React from 'react';
import { createVariantMap, toJson, shallow } from '../../../tests/globalImports';
import { Table, TableProps } from './table';
import tableVariants from './table.json';

describe('Table component should be rendered correctly', () => {
  it('All variants', () => {

    const variantMap: { [key: string]: TableProps } = createVariantMap<TableProps>(tableVariants, TableProps);
    Object.entries(variantMap).forEach(([key, value]) => {
        const icon = shallow(<Table {...value} />);
        expect(toJson(icon)).toMatchSnapshot();
    });
  });
});