// textImage.test.tsx
import * as React from 'react';
import { createVariantMap, toJson, shallow } from '../../../tests/globalImports';
import { TextImage, TextImageProps } from './textImage';
import textImageVariants from './textImage.json';

describe('TextImage component should be rendered correctly', () => {
  it('All variants', () => {

    const variantMap: { [key: string]: TextImageProps } = createVariantMap<TextImageProps>(textImageVariants, TextImageProps);
    Object.entries(variantMap).forEach(([key, value]) => {
        const icon = shallow(<TextImage {...value} />);
        expect(toJson(icon)).toMatchSnapshot();
    });
  });
});