// form.test.tsx
import * as React from 'react';
import { createVariantMap, toJson, shallow } from '../../../tests/globalImports';
import { Form, FormProps } from './form';
import formVariants from './form.json';

describe('Form component should be rendered correctly', () => {
  it('All variants', () => {

    const variantMap: { [key: string]: FormProps } = createVariantMap<FormProps>(formVariants, FormProps);
    Object.entries(variantMap).forEach(([key, value]) => {
        const icon = shallow(<Form {...value} />);
        expect(toJson(icon)).toMatchSnapshot();
    });
  });
});